mapper.update:
UPDATE t_book
SET name = #{name},isbn = #{isbn},author = #{author},
    price = #{price},publisher = #{publisher},
    status = #{status},category = #{category}
WHERE id = #{id}

UPDATE t_book
SET status = #{status}
WHERE id = #{id}

mapper.delete:
DELETE FROM t_book
WHERE id IN

    #{id}

mapper.select:
SELECT name, author, isbn, category, publisher FROM ( SELECT *, ROW_NUMBER() OVER ( PARTITION by name, author, isbn, category ORDER BY status DESC, id ) AS row_num FROM t_book
AND name LIKE CONCAT('%', #{name}, '%')
AND author LIKE CONCAT('%', #{author}, '%')
AND isbn LIKE CONCAT('%', #{isbn}, '%')
AND category = #{category}
) AS grouped_books WHERE row_num = 1
