mapper.update:
UPDATE t_book
SET isbn = #{isbn}, name = #{name}, price = #{price}, author = #{author},
    publisher = #{publisher}, category = #{category}, language = #{language},
    introduction = #{introduction}, cover_image = #{cover_image},
    total_quantity = #{total_quantity}, available_quantity = #{available_quantity}
WHERE id = #{id}

UPDATE t_book
SET available_quantity = available_quantity + #{quantity}
WHERE id = #{id}

UPDATE t_book
SET total_quantity = total_quantity + #{quantity},
    available_quantity = available_quantity + #{quantity}
WHERE id = #{id}

mapper.delete:
DELETE FROM t_book WHERE id IN
#{id}
AND status != 0
