mapper.update:
UPDATE t_lendrecord
SET status = #{status}
WHERE id = #{id}

mapper.select:
select * from t_lendrecord


        and user_id = #{user_id}


        AND book_id = #{book_id}


        AND bookname LIKE CONCAT('%', #{bookname}, '%')


        AND category LIKE CONCAT('%', #{category}, '%')



            AND status = #{status}


            AND status IN (0, 1)
